mapper.update:
update readstate
set readpage = readpage + #{readpage}
where isbn13 = #{isbn13}
  and id = #{id}

mapper.select:
select *
from memo
where id = #{loginId}
order by no desc
limit #{offset}, #{limit}

mapper.update:
update memo
set readpage = #{readpage}, ispublic = #{ispublic}, title = #{title}, content = #{content}, lastmodified = now()
where no = #{no}

update readstate
set readpage = readpage - #{readpage}
where isbn13 = #{isbn13}
and id = #{id}

mapper.select:
select count(*)
from memo
where id = #{loginId}
