mapper.select:
SELECT * FROM notice
WHERE studyboardno = #{studyboardno}
ORDER BY createdAt DESC
LIMIT #{size} OFFSET #{offset}

SELECT count(*) AS total_count
FROM notice
WHERE studyboardno = #{studyboardno}

SELECT COALESCE(MAX(noticeNo), 0)
FROM notice
WHERE studyboardno = #{studyboardno}

SELECT COUNT(*)
FROM notice
WHERE studyboardno = #{studyboardno}
